import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { GrayScaleData, BrightnessBlock } from '../utils/dataParser';

interface DataVisualizationProps {
  grayScaleData: GrayScaleData;
  brightnessBlocks: BrightnessBlock[];
}

const DataVisualization: React.FC<DataVisualizationProps> = ({ grayScaleData, brightnessBlocks }) => {
  // 准备灰阶数据用于图表显示
  const grayScaleChartData = grayScaleData.values.map((value, index) => ({
    index: index + 1,
    value,
    position: grayScaleData.positions[index]
  }));

  // 渲染亮度数据块的矩阵热力图
  const renderBrightnessBlock = (block: BrightnessBlock, blockIndex: number) => {
    const flatData = block.data.flat();
    const maxValue = Math.max(...flatData);
    const minValue = Math.min(...flatData);
    const avgValue = flatData.reduce((a, b) => a + b, 0) / flatData.length;
    const nonZeroValues = flatData.filter(v => v !== 0);

    return (
      <div key={blockIndex} className="mb-12">
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 rounded-t-2xl">
          <h3 className="text-xl font-bold">{block.label}</h3>
          <p className="text-blue-100 text-sm mt-1">32列 × 20行数据矩阵</p>
        </div>
        <div className="bg-white/90 backdrop-blur-sm p-6 rounded-b-2xl shadow-xl border border-gray-100">
          {/* 矩阵热力图 */}
          <div className="mb-6">
            <h4 className="text-lg font-semibold mb-4 text-gray-800 flex items-center">
              <svg className="w-5 h-5 mr-2 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
              数据热力图
            </h4>
            <div className="overflow-x-auto bg-gray-50 rounded-xl p-4">
              <div className="inline-block min-w-full">
                {/* 列标题 */}
                <div className="flex mb-2">
                  <div className="w-10 h-8 flex items-center justify-center text-xs font-bold text-gray-600 bg-gradient-to-r from-gray-100 to-gray-200 rounded-tl-lg"></div>
                  {Array.from({ length: 32 }, (_, i) => (
                    <div key={i} className="w-8 h-8 flex items-center justify-center text-xs font-bold text-white bg-gradient-to-r from-blue-500 to-blue-600 border-r border-blue-400 first:rounded-none last:rounded-tr-lg">
                      {String.fromCharCode(65 + i)}
                    </div>
                  ))}
                </div>
                {/* 矩阵数据 */}
                {block.data.map((row, rowIndex) => (
                  <div key={rowIndex} className="flex mb-1">
                    {/* 行标题 */}
                    <div className="w-10 h-8 flex items-center justify-center text-xs font-bold text-white bg-gradient-to-r from-purple-500 to-purple-600 border-b border-purple-400">
                      {block.startRow + rowIndex}
                    </div>
                    {/* 数据单元格 */}
                    {row.map((value, colIndex) => {
                      // 计算颜色强度 (0-1)
                      const intensity = maxValue > minValue ? (value - minValue) / (maxValue - minValue) : 0;
                      const backgroundColor = value === 0 
                        ? '#f1f5f9' 
                        : `rgba(59, 130, 246, ${0.15 + intensity * 0.85})`; // 蓝色渐变
                      const textColor = intensity > 0.6 ? '#ffffff' : '#1e293b';

                      return (
                        <div
                          key={`${rowIndex}-${colIndex}`}
                          className="w-8 h-8 flex items-center justify-center text-xs border border-gray-300 cursor-pointer hover:border-blue-400 hover:shadow-md transition-all duration-200 hover:scale-105"
                          style={{ backgroundColor, color: textColor }}
                          title={`行: ${block.startRow + rowIndex}, 列: ${String.fromCharCode(65 + colIndex)}, 值: ${value.toFixed(6)}`}
                        >
                          {value !== 0 && (
                            <span className="font-mono font-semibold" style={{ fontSize: '9px' }}>
                               {Math.abs(value) >= 1000 ? value.toExponential(2) : value.toFixed(2)}
                             </span>
                          )}
                        </div>
                      );
                    })}
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* 数据统计 */}
          <div className="mt-8">
            <h5 className="text-md font-semibold mb-4 text-gray-800 flex items-center">
              <svg className="w-4 h-4 mr-2 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
              </svg>
              数据统计
            </h5>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 p-4 bg-gradient-to-r from-gray-50 to-blue-50 rounded-xl border border-gray-200">
              <div className="text-center p-3 bg-white rounded-lg shadow-sm">
                <div className="text-xs font-medium text-gray-500 mb-1">最小值</div>
                <div className="font-mono text-lg font-bold text-red-600">{minValue.toFixed(6)}</div>
              </div>
              <div className="text-center p-3 bg-white rounded-lg shadow-sm">
                <div className="text-xs font-medium text-gray-500 mb-1">最大值</div>
                <div className="font-mono text-lg font-bold text-green-600">{maxValue.toFixed(6)}</div>
              </div>
              <div className="text-center p-3 bg-white rounded-lg shadow-sm">
                <div className="text-xs font-medium text-gray-500 mb-1">平均值</div>
                <div className="font-mono text-lg font-bold text-blue-600">{avgValue.toFixed(6)}</div>
              </div>
              <div className="text-center p-3 bg-white rounded-lg shadow-sm">
                <div className="text-xs font-medium text-gray-500 mb-1">非零值</div>
                <div className="font-mono text-lg font-bold text-purple-600">{nonZeroValues.length}/{flatData.length}</div>
              </div>
            </div>
          </div>

          {/* 数值分布图例 */}
          <div className="mt-6 p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-200">
            <h5 className="text-sm font-semibold text-gray-800 mb-3 flex items-center">
              <svg className="w-4 h-4 mr-2 text-indigo-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
              </svg>
              颜色图例
            </h5>
            <div className="flex items-center justify-between text-xs text-gray-700">
              <div className="flex items-center bg-white px-3 py-2 rounded-lg shadow-sm">
                <div className="w-5 h-5 bg-slate-100 border-2 border-gray-300 rounded mr-2"></div>
                <span className="font-medium">零值</span>
              </div>
              <div className="flex items-center bg-white px-3 py-2 rounded-lg shadow-sm">
                <div className="w-5 h-5 bg-blue-200 rounded mr-2"></div>
                <span className="font-medium">低值</span>
              </div>
              <div className="flex items-center bg-white px-3 py-2 rounded-lg shadow-sm">
                <div className="w-5 h-5 bg-blue-500 rounded mr-2"></div>
                <span className="font-medium">中值</span>
              </div>
              <div className="flex items-center bg-white px-3 py-2 rounded-lg shadow-sm">
                <div className="w-5 h-5 bg-blue-800 rounded mr-2"></div>
                <span className="font-medium">高值</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-8">
      {/* 灰阶数据可视化 */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4 text-gray-800">灰阶数据 (N维向量)</h2>
        <div className="mb-4">
          <p className="text-sm text-gray-600 mb-2">
            数据点数量: {grayScaleData.values.length} | 
            起始位置: B1 | 
            间隔: 每22个单元格
          </p>
        </div>

        {grayScaleData.values.length > 0 ? (
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={grayScaleChartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="index" 
                label={{ value: '数据点索引', position: 'insideBottom', offset: -10 }}
              />
              <YAxis 
                label={{ value: '灰阶值', angle: -90, position: 'insideLeft' }}
              />
              <Tooltip 
                formatter={(value, name, props) => [
                  `值: ${Number(value).toFixed(6)}`,
                  `位置: ${props.payload.position}`
                ]}
                labelFormatter={(index) => `数据点 ${index}`}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="value" 
                stroke="#8884d8" 
                strokeWidth={2}
                dot={{ fill: '#8884d8', strokeWidth: 2, r: 4 }}
                name="灰阶值"
              />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <div className="text-center py-8 text-gray-500">
            未找到灰阶数据
          </div>
        )}

        {/* 数据详情表格 */}
        {grayScaleData.values.length > 0 && (
          <div className="mt-6">
            <h3 className="text-lg font-semibold mb-3 text-gray-800">数据详情</h3>
            <div className="max-h-40 overflow-y-auto border rounded">
              <table className="w-full text-sm">
                <thead className="bg-gray-50 sticky top-0">
                  <tr>
                    <th className="px-4 py-2 text-left">索引</th>
                    <th className="px-4 py-2 text-left">位置</th>
                    <th className="px-4 py-2 text-left">值</th>
                  </tr>
                </thead>
                <tbody>
                  {grayScaleData.values.map((value, index) => (
                    <tr key={index} className="border-t hover:bg-gray-50">
                      <td className="px-4 py-2">{index + 1}</td>
                      <td className="px-4 py-2 font-mono">{grayScaleData.positions[index]}</td>
                      <td className="px-4 py-2 font-mono">{value.toFixed(6)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>

      {/* 亮度数据块可视化 */}
      <div>
        <h2 className="text-xl font-bold mb-6 text-gray-800">亮度数据块 (32×20 N维向量)</h2>
        {brightnessBlocks.length > 0 ? (
          brightnessBlocks.map((block, index) => renderBrightnessBlock(block, index))
        ) : (
          <div className="bg-white p-6 rounded-lg shadow-md text-center text-gray-500">
            未找到亮度数据块
          </div>
        )}
      </div>

      {/* 数据统计摘要 */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-bold mb-4 text-gray-800">数据统计摘要</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-blue-50 p-4 rounded-lg">
            <h3 className="font-semibold text-blue-800">灰阶数据</h3>
            <p className="text-sm text-blue-600 mt-1">
              数据点: {grayScaleData.values.length}
            </p>
            {grayScaleData.values.length > 0 && (
              <>
                <p className="text-sm text-blue-600">
                  平均值: {(grayScaleData.values.reduce((a, b) => a + b, 0) / grayScaleData.values.length).toFixed(6)}
                </p>
                <p className="text-sm text-blue-600">
                  范围: {Math.min(...grayScaleData.values).toFixed(6)} - {Math.max(...grayScaleData.values).toFixed(6)}
                </p>
              </>
            )}
          </div>

          <div className="bg-green-50 p-4 rounded-lg">
            <h3 className="font-semibold text-green-800">亮度数据块</h3>
            <p className="text-sm text-green-600 mt-1">
              数据块数量: {brightnessBlocks.length}
            </p>
            <p className="text-sm text-green-600">
              每块维度: 32 × 20
            </p>
            <p className="text-sm text-green-600">
              总数据点: {brightnessBlocks.length * 32 * 20}
            </p>
          </div>

          <div className="bg-purple-50 p-4 rounded-lg">
            <h3 className="font-semibold text-purple-800">解析配置</h3>
            <p className="text-sm text-purple-600 mt-1">
              灰阶起始: B1
            </p>
            <p className="text-sm text-purple-600">
              灰阶间隔: 22个单元格
            </p>
            <p className="text-sm text-purple-600">
              亮度块间隔: 22行
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DataVisualization;
