import React, { useState, useCallback } from 'react';
import * as XLSX from 'xlsx';
import { FittingResult, performAllFittings } from '../utils/curveFitting';
import { ParsedData } from '../utils/dataParser';

interface TargetData {
  values: number[];
  positions: string[];
}

interface PredictionResult {
  targetValue: number;
  position: string;
  predictedGrayScale: number;
  bestFittingType: string;
  confidence: number;
}

interface TargetDataPredictionProps {
  grayScaleData: {
    values: number[];
    positions: string[];
  };
  brightnessBlocks: Array<{
    label: string;
    startRow: number;
    endRow: number;
    data: number[][];
    centerPixelValue: number;
    normalizedData?: number[][];
  }>;
}

const TargetDataPrediction: React.FC<TargetDataPredictionProps> = ({ grayScaleData, brightnessBlocks }) => {
  const [targetData, setTargetData] = useState<TargetData | null>(null);
  const [predictionResults, setPredictionResults] = useState<PredictionResult[]>([]);
  const [bestFittingResult, setBestFittingResult] = useState<FittingResult | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // 读取Excel Sheet3数据
  const handleFileUpload = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsLoading(true);
    setError(null);

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const data = new Uint8Array(e.target?.result as ArrayBuffer);
        const workbook = XLSX.read(data, { type: 'array' });

        // 检查是否有Sheet3
        if (!workbook.SheetNames.includes('Sheet3')) {
          throw new Error('未找到Sheet3工作表');
        }

        const worksheet = workbook.Sheets['Sheet3'];
        const values: number[] = [];
        const positions: string[] = [];

        // 从B1开始读取目标数据，每隔22个单元格获取下一个值
        let currentRow = 1;
        const column = 'B';

        while (true) {
          const cellAddress = `${column}${currentRow}`;
          const cell = worksheet[cellAddress];

          if (!cell || cell.v === undefined || cell.v === null || cell.v === '') {
            break;
          }

          const value = typeof cell.v === 'number' ? cell.v : parseFloat(cell.v.toString());
          if (!isNaN(value)) {
            values.push(value);
            positions.push(cellAddress);
          }

          currentRow += 22;
        }

        if (values.length === 0) {
          throw new Error('Sheet3中未找到有效的目标数据');
        }

        setTargetData({ values, positions });
      } catch (error) {
        setError(error instanceof Error ? error.message : '文件读取失败');
      } finally {
        setIsLoading(false);
      }
    };

    reader.onerror = () => {
      setError('文件读取失败');
      setIsLoading(false);
    };

    reader.readAsArrayBuffer(file);
  }, []);

  // 获取最佳拟合结果
  const getBestFittingResult = useCallback((): FittingResult | null => {
    if (!grayScaleData.values.length || !brightnessBlocks.length) {
      return null;
    }

    // 提取中心像素值和灰阶值
    const centerPixelValues = brightnessBlocks.map(block => block.centerPixelValue);
    const grayScaleValues = grayScaleData.values;

    if (centerPixelValues.length !== grayScaleValues.length) {
      return null;
    }

    // 执行所有拟合算法
    const fittingResults = performAllFittings({
      x: centerPixelValues,
      y: grayScaleValues
    });

    // 找到R²值最高的拟合结果
    const bestResult = fittingResults.reduce((best, current) => {
      if (isNaN(current.rSquared) || current.rSquared < 0) return best;
      if (!best || current.rSquared > best.rSquared) return current;
      return best;
    }, null as FittingResult | null);

    return bestResult;
  }, [grayScaleData, brightnessBlocks]);

  // 生成预测结果
  const generatePredictions = useCallback(() => {
    if (!targetData) {
      setError('请先上传目标数据');
      return;
    }

    const bestFitting = getBestFittingResult();
    if (!bestFitting) {
      setError('无法获取有效的拟合结果');
      return;
    }

    setBestFittingResult(bestFitting);

    const predictions: PredictionResult[] = targetData.values.map((targetValue, index) => {
      let predictedGrayScale = 0;

      // 根据拟合类型计算预测值
      switch (bestFitting.type) {
        case 'logarithmic':
          if (targetValue > 0) {
            const [a, b] = bestFitting.coefficients;
            predictedGrayScale = a * Math.log(targetValue) + b;
          }
          break;
        case 'exponential':
          const [a_exp, b_exp] = bestFitting.coefficients;
          predictedGrayScale = a_exp * Math.exp(b_exp * targetValue);
          break;
        case 'polynomial':
          const [a_poly, b_poly, c_poly, d_poly] = bestFitting.coefficients;
          predictedGrayScale = a_poly * Math.pow(targetValue, 3) + 
                              b_poly * Math.pow(targetValue, 2) + 
                              c_poly * targetValue + d_poly;
          break;
        case 'power':
          if (targetValue > 0) {
            const [a_pow, b_pow] = bestFitting.coefficients;
            predictedGrayScale = a_pow * Math.pow(targetValue, b_pow);
          }
          break;
        case 'bivariate':
          const [a_biv, b_biv, c_biv] = bestFitting.coefficients;
          predictedGrayScale = a_biv * Math.pow(targetValue, 2) + 
                              b_biv * targetValue + c_biv;
          break;
      }

      return {
        targetValue,
        position: targetData.positions[index],
        predictedGrayScale,
        bestFittingType: bestFitting.type,
        confidence: bestFitting.rSquared
      };
    });

    setPredictionResults(predictions);
    setError(null);
  }, [targetData, getBestFittingResult]);

  // 导出预测结果到Excel
  const exportToExcel = useCallback(() => {
    if (predictionResults.length === 0) {
      setError('没有预测结果可导出');
      return;
    }

    const workbook = XLSX.utils.book_new();

    // 创建预测结果工作表
    const wsData = [
      ['位置', '目标数据值', '预测灰阶值', '拟合类型', '置信度(R²)'],
      ...predictionResults.map(result => [
        result.position,
        result.targetValue,
        result.predictedGrayScale.toFixed(6),
        getTypeLabel(result.bestFittingType),
        result.confidence.toFixed(6)
      ])
    ];

    const worksheet = XLSX.utils.aoa_to_sheet(wsData);
    XLSX.utils.book_append_sheet(workbook, worksheet, '预测结果');

    // 添加拟合信息工作表
    if (bestFittingResult) {
      const fittingInfo = [
        ['拟合信息', ''],
        ['拟合类型', getTypeLabel(bestFittingResult.type)],
        ['拟合公式', bestFittingResult.formula],
        ['R²值', bestFittingResult.rSquared.toFixed(6)],
        ['RMSE', bestFittingResult.rmse.toFixed(6)],
        ['MAE', bestFittingResult.mae.toFixed(6)],
        ['最大误差', bestFittingResult.maxError.toFixed(6)]
      ];

      const fittingWs = XLSX.utils.aoa_to_sheet(fittingInfo);
      XLSX.utils.book_append_sheet(workbook, fittingWs, '拟合信息');
    }

    // 下载文件
    const fileName = `目标数据预测结果_${new Date().toISOString().slice(0, 10)}.xlsx`;
    XLSX.writeFile(workbook, fileName);
  }, [predictionResults, bestFittingResult]);

  // 获取拟合类型标签
  const getTypeLabel = (type: string) => {
    const labels: { [key: string]: string } = {
      'logarithmic': '对数拟合',
      'exponential': '指数拟合',
      'polynomial': '三次多项式拟合',
      'power': '幂函数拟合',
      'bivariate': '二次多项式拟合'
    };
    return labels[type] || type;
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-6">
      <h3 className="text-lg font-semibold mb-6">目标数据预测</h3>

      {/* 文件上传区域 */}
      <div className="mb-6">
        <h4 className="font-medium mb-3">上传目标数据文件 (Sheet3)</h4>
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
          <input
            type="file"
            accept=".xlsx,.xls"
            onChange={handleFileUpload}
            className="hidden"
            id="target-file-upload"
          />
          <label
            htmlFor="target-file-upload"
            className="cursor-pointer inline-flex items-center px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors"
          >
            选择Excel文件
          </label>
          <p className="mt-2 text-sm text-gray-500">
            请选择包含Sheet3工作表的Excel文件，目标数据应位于B列
          </p>
        </div>
      </div>

      {/* 加载状态 */}
      {isLoading && (
        <div className="mb-4 p-4 bg-blue-50 border border-blue-200 rounded-lg">
          <p className="text-blue-700">正在读取文件...</p>
        </div>
      )}

      {/* 错误信息 */}
      {error && (
        <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
          <p className="text-red-700">{error}</p>
        </div>
      )}

      {/* 目标数据概览 */}
      {targetData && (
        <div className="mb-6 p-4 bg-gray-50 rounded-lg">
          <h4 className="font-medium mb-2">目标数据概览</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-gray-600">数据点数量：</span>
              <span className="font-medium">{targetData.values.length}</span>
            </div>
            <div>
              <span className="text-gray-600">数值范围：</span>
              <span className="font-medium">
                {Math.min(...targetData.values).toFixed(2)} - {Math.max(...targetData.values).toFixed(2)}
              </span>
            </div>
          </div>
        </div>
      )}

      {/* 生成预测按钮 */}
      {targetData && parsedData && (
        <div className="mb-6">
          <button
            onClick={generatePredictions}
            className="px-6 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors"
          >
            生成预测结果
          </button>
        </div>
      )}

      {/* 最佳拟合信息 */}
      {bestFittingResult && (
        <div className="mb-6 p-4 bg-blue-50 rounded-lg">
          <h4 className="font-medium mb-2">使用的最佳拟合模型</h4>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-gray-600">拟合类型：</span>
              <span className="font-medium">{getTypeLabel(bestFittingResult.type)}</span>
            </div>
            <div>
              <span className="text-gray-600">R²值：</span>
              <span className="font-medium">{bestFittingResult.rSquared.toFixed(6)}</span>
            </div>
            <div className="col-span-2">
              <span className="text-gray-600">拟合公式：</span>
              <span className="font-medium font-mono">{bestFittingResult.formula}</span>
            </div>
          </div>
        </div>
      )}

      {/* 预测结果表格 */}
      {predictionResults.length > 0 && (
        <div className="mb-6">
          <div className="flex justify-between items-center mb-3">
            <h4 className="font-medium">预测结果</h4>
            <button
              onClick={exportToExcel}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors text-sm"
            >
              导出到Excel
            </button>
          </div>

          <div className="overflow-x-auto">
            <table className="w-full text-sm border-collapse border border-gray-200">
              <thead>
                <tr className="bg-gray-50">
                  <th className="border border-gray-200 px-3 py-2 text-left">位置</th>
                  <th className="border border-gray-200 px-3 py-2 text-left">目标数据值</th>
                  <th className="border border-gray-200 px-3 py-2 text-left">预测灰阶值</th>
                  <th className="border border-gray-200 px-3 py-2 text-left">拟合类型</th>
                  <th className="border border-gray-200 px-3 py-2 text-left">置信度</th>
                </tr>
              </thead>
              <tbody>
                {predictionResults.map((result, index) => (
                  <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                    <td className="border border-gray-200 px-3 py-2 font-mono">{result.position}</td>
                    <td className="border border-gray-200 px-3 py-2">{result.targetValue.toFixed(2)}</td>
                    <td className="border border-gray-200 px-3 py-2 font-medium">
                      {result.predictedGrayScale.toFixed(6)}
                    </td>
                    <td className="border border-gray-200 px-3 py-2">
                      {getTypeLabel(result.bestFittingType)}
                    </td>
                    <td className="border border-gray-200 px-3 py-2">
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        result.confidence > 0.9 ? 'bg-green-100 text-green-800' :
                        result.confidence > 0.7 ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {result.confidence.toFixed(4)}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* 统计信息 */}
          <div className="mt-4 p-4 bg-gray-50 rounded-lg">
            <h5 className="font-medium mb-2">预测统计</h5>
            <div className="grid grid-cols-3 gap-4 text-sm">
              <div>
                <span className="text-gray-600">预测数据点：</span>
                <span className="font-medium">{predictionResults.length}</span>
              </div>
              <div>
                <span className="text-gray-600">平均预测值：</span>
                <span className="font-medium">
                  {(predictionResults.reduce((sum, r) => sum + r.predictedGrayScale, 0) / predictionResults.length).toFixed(4)}
                </span>
              </div>
              <div>
                <span className="text-gray-600">预测范围：</span>
                <span className="font-medium">
                  {Math.min(...predictionResults.map(r => r.predictedGrayScale)).toFixed(4)} - 
                  {Math.max(...predictionResults.map(r => r.predictedGrayScale)).toFixed(4)}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* 使用说明 */}
      {!targetData && (
        <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
          <h4 className="font-medium mb-2 text-yellow-800">使用说明</h4>
          <ul className="text-sm text-yellow-700 space-y-1">
            <li>1. 首先确保已上传并处理了原始数据文件</li>
            <li>2. 上传包含Sheet3工作表的Excel文件，目标数据应位于B列</li>
            <li>3. 系统将自动选择最佳拟合模型进行预测</li>
            <li>4. 生成预测结果后可导出到Excel文件</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default TargetDataPrediction;
