import React, { useCallback, useState } from 'react';

interface FileUploadProps {
  onFileSelect: (file: File) => void;
  isLoading: boolean;
}

const FileUpload: React.FC<FileUploadProps> = ({ onFileSelect, isLoading }) => {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFileName, setSelectedFileName] = useState<string>('');

  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];
      if (isExcelFile(file)) {
        setSelectedFileName(file.name);
        onFileSelect(file);
      } else {
        alert('请选择Excel文件 (.xlsx, .xls)');
      }
    }
  }, [onFileSelect]);

  const handleFileChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      if (isExcelFile(file)) {
        setSelectedFileName(file.name);
        onFileSelect(file);
      } else {
        alert('请选择Excel文件 (.xlsx, .xls)');
        e.target.value = ''; // 清空输入
      }
    }
  }, [onFileSelect]);

  const isExcelFile = (file: File): boolean => {
    const allowedTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/vnd.ms-excel', // .xls
    ];
    const allowedExtensions = ['.xlsx', '.xls'];

    return allowedTypes.includes(file.type) || 
           allowedExtensions.some(ext => file.name.toLowerCase().endsWith(ext));
  };

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="mb-4">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Excel数据解析器</h2>
        <p className="text-gray-600">
          上传Excel文件以解析灰阶数据和亮度数据块
        </p>
      </div>

      <div
        className={`relative border-2 border-dashed rounded-lg p-8 text-center transition-colors duration-200 ${
          dragActive
            ? 'border-blue-400 bg-blue-50'
            : 'border-gray-300 hover:border-gray-400'
        } ${isLoading ? 'opacity-50 pointer-events-none' : ''}`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
      >
        <input
          type="file"
          id="file-upload"
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          accept=".xlsx,.xls"
          onChange={handleFileChange}
          disabled={isLoading}
        />

        <div className="space-y-4">
          {isLoading ? (
            <div className="flex flex-col items-center">
              <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mb-4"></div>
              <p className="text-lg font-medium text-gray-700">正在解析数据...</p>
              <p className="text-sm text-gray-500">请稍候，正在处理您的Excel文件</p>
            </div>
          ) : (
            <>
              <div className="flex justify-center">
                <svg
                  className="w-16 h-16 text-gray-400"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12"
                  />
                </svg>
              </div>

              <div>
                <p className="text-lg font-medium text-gray-700 mb-2">
                  {selectedFileName ? `已选择: ${selectedFileName}` : '拖拽Excel文件到此处'}
                </p>
                <p className="text-sm text-gray-500 mb-4">
                  或者 <span className="text-blue-600 font-medium">点击选择文件</span>
                </p>
                <p className="text-xs text-gray-400">
                  支持格式: .xlsx, .xls
                </p>
              </div>

              {selectedFileName && (
                <button
                  type="button"
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
                  onClick={() => {
                    const input = document.getElementById('file-upload') as HTMLInputElement;
                    if (input.files && input.files[0]) {
                      onFileSelect(input.files[0]);
                    }
                  }}
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12" />
                  </svg>
                  重新解析数据
                </button>
              )}
            </>
          )}
        </div>
      </div>

      {/* 解析说明 */}
      <div className="mt-6 bg-gray-50 rounded-lg p-4">
        <h3 className="text-sm font-semibold text-gray-800 mb-2">数据解析说明:</h3>
        <ul className="text-sm text-gray-600 space-y-1">
          <li>• <strong>灰阶数据:</strong> 从B1单元格开始，每隔22个单元格获取一个值，直到遇到空单元格</li>
          <li>• <strong>亮度数据块:</strong> 解析A2-AF21和A24-AF43区域，每个区域为32×20的数据矩阵</li>
          <li>• <strong>输出格式:</strong> 灰阶数据为N维向量，亮度数据为32×20的N维向量集合</li>
        </ul>
      </div>
    </div>
  );
};

export default FileUpload;
