import React, { useState, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { GrayScaleData, BrightnessBlock } from '../utils/dataParser';
import CurveFittingVisualization from './CurveFittingVisualization';

interface DataVisualizationProps {
  grayScaleData: GrayScaleData;
  brightnessBlocks: BrightnessBlock[];
}

const DataVisualization: React.FC<DataVisualizationProps> = ({ grayScaleData, brightnessBlocks }) => {
  const [showNormalizedData, setShowNormalizedData] = useState(false);

  // 提取中心像素值和灰阶值用于曲线拟合
  const { centerPixelValues, grayScaleValues } = useMemo(() => {
    const centerValues = brightnessBlocks.map(block => block.centerPixelValue || 0);
    const grayValues = grayScaleData.values.slice(0, brightnessBlocks.length);
    return {
      centerPixelValues: centerValues,
      grayScaleValues: grayValues
    };
  }, [brightnessBlocks, grayScaleData]);
  // 准备灰阶数据用于图表显示
  const grayScaleChartData = grayScaleData.values.map((value, index) => ({
    index: index + 1,
    value,
    position: grayScaleData.positions[index]
  }));

  // 渲染亮度数据块的矩阵热力图
  const renderBrightnessBlock = (block: BrightnessBlock, blockIndex: number) => {
    const currentData = showNormalizedData && block.normalizedData ? block.normalizedData : block.data;
    const flatData = currentData.flat();
    const maxValue = Math.max(...flatData);
    const minValue = Math.min(...flatData);
    const avgValue = flatData.reduce((a, b) => a + b, 0) / flatData.length;
    const nonZeroValues = flatData.filter(v => v !== 0);

    return (
      <div key={blockIndex} className="mb-12">
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white p-4 rounded-t-2xl">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-xl font-bold">{block.label}</h3>
              <p className="text-blue-100 text-sm mt-1">32列 × 20行数据矩阵</p>
            </div>
            {/* 显示对应的灰阶数据范围 */}
            {grayScaleData && grayScaleData.values.length > 0 && (
              <div className="text-sm bg-white bg-opacity-20 rounded-lg px-3 py-2">
                <div className="font-medium mb-1">对应灰阶数据:</div>
                <div className="font-mono text-xs">
                  {(() => {
                    const startIndex = Math.floor((block.startRow - 1) / 22);
                    const endIndex = Math.min(startIndex + block.data.length - 1, grayScaleData.values.length - 1);
                    if (startIndex < grayScaleData.values.length) {
                      const correspondingGrayscale = grayScaleData.values.slice(startIndex, endIndex + 1);
                      if (correspondingGrayscale.length > 0) {
                        const avgGrayscale = correspondingGrayscale.reduce((sum, val) => sum + val, 0) / correspondingGrayscale.length;
                        const minGrayscale = Math.min(...correspondingGrayscale);
                        const maxGrayscale = Math.max(...correspondingGrayscale);
                        return `范围: ${minGrayscale.toFixed(4)} ~ ${maxGrayscale.toFixed(4)}\n均值: ${avgGrayscale.toFixed(4)}`;
                      }
                    }
                    return '无对应数据';
                  })()
                  }
                </div>
              </div>
            )}
          </div>
        </div>
