import React, { useState, useCallback } from 'react';
import FileUpload from './components/FileUpload';
import DataVisualization from './components/DataVisualization';
import { parseExcelData, ParsedData } from './utils/dataParser';

const App: React.FC = () => {
  const [parsedData, setParsedData] = useState<ParsedData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleFileSelect = useCallback(async (file: File) => {
    setIsLoading(true);
    setError(null);
    setParsedData(null);

    try {
      const data = await parseExcelData(file);
      setParsedData(data);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : '解析文件时发生未知错误';
      setError(errorMessage);
      console.error('解析错误:', err);
    } finally {
      setIsLoading(false);
    }
  }, []);

  const handleReset = useCallback(() => {
    setParsedData(null);
    setError(null);
  }, []);

  const handleExportData = useCallback(() => {
    if (!parsedData) return;

    const exportData = {
      timestamp: new Date().toISOString(),
      grayScaleData: {
        count: parsedData.grayScale.values.length,
        values: parsedData.grayScale.values,
        positions: parsedData.grayScale.positions,
        statistics: {
          min: Math.min(...parsedData.grayScale.values),
          max: Math.max(...parsedData.grayScale.values),
          average: parsedData.grayScale.values.reduce((a, b) => a + b, 0) / parsedData.grayScale.values.length
        }
      },
      brightnessBlocks: parsedData.brightnessBlocks.map(block => ({
        label: block.label,
        startRow: block.startRow,
        endRow: block.endRow,
        dimensions: `${block.data[0]?.length || 0} × ${block.data.length}`,
        data: block.data,
        statistics: {
          min: Math.min(...block.data.flat()),
          max: Math.max(...block.data.flat()),
          average: block.data.flat().reduce((a, b) => a + b, 0) / block.data.flat().length
        }
      }))
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json'
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `fitdata-export-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }, [parsedData]);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* 头部 */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">FitData Visualizer</h1>
              <p className="text-sm text-gray-600 mt-1">Excel数据解析与可视化工具</p>
            </div>
            {parsedData && (
              <div className="flex space-x-3">
                <button
                  onClick={handleExportData}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transition-colors duration-200"
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                  导出数据
                </button>
                <button
                  onClick={handleReset}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transition-colors duration-200"
                >
                  <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                  </svg>
                  重新开始
                </button>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* 主内容 */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-md p-4">
            <div className="flex">
              <div className="flex-shrink-0">
                <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                  <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                </svg>
              </div>
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">解析错误</h3>
                <div className="mt-2 text-sm text-red-700">
                  <p>{error}</p>
                </div>
                <div className="mt-4">
                  <button
                    type="button"
                    className="bg-red-50 text-red-800 rounded-md p-2 inline-flex items-center text-sm font-medium hover:bg-red-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    onClick={() => setError(null)}
                  >
                    关闭
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {!parsedData ? (
          <FileUpload onFileSelect={handleFileSelect} isLoading={isLoading} />
        ) : (
          <DataVisualization 
            grayScaleData={parsedData.grayScale} 
            brightnessBlocks={parsedData.brightnessBlocks} 
          />
        )}
      </main>

      {/* 页脚 */}
      <footer className="bg-white border-t mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="text-center text-sm text-gray-500">
            <p>FitData Visualizer - 专业的Excel数据解析与可视化工具</p>
            <p className="mt-1">支持灰阶数据和亮度数据块的智能解析与可视化展示</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
